ine alt2\" style=\"padding:0px !important;margin:0px !important;border:0px !important;background-color:#F8F8F8 !important;vertical-align:baseline !important;font-size:10pt !important;\">\r\n				<table style=\"padding:0px !important;margin:0px !important;border:0px !important;height:auto !important;width:auto !important;font-size:10pt !important;\">\r\n					<tbody>\r\n						<tr>\r\n							<td class=\"number\" style=\"border:0px !important;vertical-align:top !important;font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace !important;font-size:10pt !important;color:#AFAFAF !important;\">\r\n								06\r\n							</td>\r\n							<td class=\"content\" style=\"vertical-align:top !important;font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace !important;font-size:10pt !important;\">\r\n								client_body_buffer_size 128k;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</div>\r\n			<div class=\"line alt1\" style=\"padding:0px !important;margin:0px !important;border:0px !important;vertical-align:baseline !important;font-size:10pt !important;\">\r\n				<table style=\"padding:0px !important;margin:0px !important;border:0px !important;height:auto !important;width:auto !important;font-size:10pt !important;\">\r\n					<tbody>\r\n						<tr>\r\n							<td class=\"number\" style=\"border:0px !important;vertical-align:top !important;font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace !important;font-size:10pt !important;color:#AFAFAF !important;\">\r\n								07\r\n							</td>\r\n							<td class=\"content\" style=\"vertical-align:top !important;font-family:Consolas, \'Bitstream Vera Sans Mono\', \'Courier New\', Courier, monospace !important;font-size:10pt !important;\">\r\n								proxy_connect_timeout&nbsp;&nbsp; 300;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</div>\r\n			<div class=\"line alt2\" style=\"padding:0px !important